import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "../estilos/Sidebar.css";
import sidebarConfig from "../usuarios/administrador/configuracion/sidebarConfig";
import { FaArrowLeft, FaArrowRight, FaChevronDown, FaChevronRight } from "react-icons/fa";
import logo from "../recursos/logo.png";

const Sidebar = () => {
    const [isCollapsed, setIsCollapsed] = useState(false);
    const [activeMenu, setActiveMenu] = useState(null);

    const toggleSidebar = () => {
        setIsCollapsed(!isCollapsed);
        setActiveMenu(null); // Cierra cualquier submenú abierto
    };

    const toggleSubMenu = (index) => {
        setActiveMenu(activeMenu === index ? null : index);
    };

    return (
        <>
            <button
                className="toggle-btn-circle"
                onClick={toggleSidebar}
                style={{ left: isCollapsed ? "70px" : "250px" }}
                title={isCollapsed ? "Expandir" : "Colapsar"}
            >
                {isCollapsed ? <FaArrowRight /> : <FaArrowLeft />}
            </button>
            <div className={`sidebar-container ${isCollapsed ? "collapsed" : ""}`}>
                <div className={`sidebar ${isCollapsed ? "collapsed" : ""}`}>
                    <div className="sidebar-header">
                        <img src={logo} alt="Logo" className="sidebar-logo" />
                        {!isCollapsed && <h3 className="sidebar-title">Parques de la Huaca</h3>}
                    </div>
                    <div className="sidebar-main-content">
                        <ul className="nav">
                            {sidebarConfig.map((item, index) => (
                                <li
                                    key={index}
                                    className={`nav-item ${item.subItems ? "has-submenu" : ""}`}
                                    onClick={(e) => {
                                        if (item.subItems) {
                                            e.preventDefault(); // Evita la navegación directa
                                            toggleSubMenu(index);
                                        }
                                    }}
                                    title={isCollapsed ? item.label : ""}
                                >
                                    <NavLink to={item.path} className="nav-link" activeClassName="active">
                                        <i className="nav-icon">{item.icon}</i>
                                        {!isCollapsed && <span className="nav-label">{item.label}</span>}
                                        {item.subItems && !isCollapsed && (
                                            <i className="submenu-icon">
                                                {activeMenu === index ? <FaChevronDown /> : <FaChevronRight />}
                                            </i>
                                        )}
                                    </NavLink>
                                    {item.subItems && activeMenu === index && (
                                        <ul className="nav sub-menu">
                                            {item.subItems.map((subItem, subIndex) => (
                                                <li key={subIndex} className="nav-item">
                                                    <NavLink
                                                        to={subItem.path}
                                                        className="nav-link"
                                                        activeClassName="active"
                                                    >
                                                        {subItem.label}
                                                    </NavLink>
                                                </li>
                                            ))}
                                        </ul>
                                    )}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Sidebar;
