// src/componentes-comunes/Sidebar.js
import React from 'react';
import { useLocation } from 'react-router-dom';
import './SidebarGlobal.css'; // Verifica la ruta y si el CSS est치 bien cargado

const Sidebar = ({ data }) => {
    const location = useLocation(); // Hook para obtener la ubicaci칩n actual

    return (
        <div className="sidebar-container">
            <div className="sidebar">
                {data.map((item) => (
                    <div
                        key={item.id}
                        className={`nav-item ${location.pathname === item.route ? 'active' : ''}`}
                    >
                        <i className={`fa ${item.icon}`} style={{ marginRight: "10px" }}></i>
                        <span className="nav-label">{item.label}</span>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Sidebar;
