import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import '../estilos/Sidebar.css';

// Importar la configuración específica del usuario
import sidebarConfig from '../usuarios/administrador/configuracion/sidebarConfig';

const Sidebar = ({ userRole }) => {
    const [isCollapsed, setIsCollapsed] = useState(false);

    const toggleSidebar = () => {
        setIsCollapsed(!isCollapsed);
    };

    return (
        <>
            <button
                className="toggle-btn-circle"
                onClick={toggleSidebar}
                style={{ left: isCollapsed ? '70px' : '250px' }}
            >
                <i className={`fas ${isCollapsed ? 'fa-arrow-right' : 'fa-arrow-left'}`}></i>
            </button>
            <div className={`sidebar-container ${isCollapsed ? 'collapsed' : ''}`}>
                <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
                    <div className="sidebar-header">
                        <img
                            src="/ruta/a/tu/logo.jpg"
                            alt="Logo"
                            className="sidebar-logo"
                        />
                        <h3>Administrador</h3>
                    </div>
                    <div className="sidebar-main-content">
                        <ul className="nav">
                            {sidebarConfig.map((item, index) => (
                                <li key={index} className="nav-item">
                                    <NavLink to={item.path} className="nav-link" activeClassName="active">
                                        <i className={`nav-icon ${item.icon}`}></i>
                                        <span className="nav-label">{item.label}</span>
                                    </NavLink>
                                    {item.subItems && (
                                        <ul className="nav sub-menu">
                                            {item.subItems.map((subItem, subIndex) => (
                                                <li key={subIndex} className="nav-item">
                                                    <NavLink to={subItem.path} className="nav-link" activeClassName="active">
                                                        <span className="nav-label">{subItem.label}</span>
                                                    </NavLink>
                                                </li>
                                            ))}
                                        </ul>
                                    )}
                                </li>
                            ))}
                        </ul>
                    </div>
                    {/* Si tienes elementos en el footer del sidebar */}
                    <div className="sidebar-footer">
                        {/* Opcional: Agregar elementos en el footer */}
                    </div>
                </div>
            </div>
        </>
    );
};

export default Sidebar;
