import React from "react";
import { Link, useLocation } from "react-router-dom";
import { Tooltip, OverlayTrigger } from "react-bootstrap";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";
import "./SidebarGlobal.css";

const Sidebar = ({ sidebarData, isCollapsed, toggleSidebar }) => {
    const location = useLocation();

    // Renderiza un Tooltip dinámico
    const renderTooltip = (text) => (
        <Tooltip id="sidebar-tooltip" className="custom-tooltip">
            {text}
        </Tooltip>
    );

    return (
        <div className="sidebar-container">
            {/* Sidebar principal */}
            <div className={`sidebar ${isCollapsed ? "collapsed" : ""}`}>
                {/* Header del Sidebar */}
                <div className="sidebar-header">
                    <img
                        src={sidebarData.logo}
                        alt="Logo"
                        className="sidebar-logo"
                    />
                    {!isCollapsed && <h3>{sidebarData.title}</h3>}
                </div>

                {/* Elementos principales */}
                <div className="sidebar-main-content">
                    <ul className="nav">
                        {sidebarData.navItems.map((item, index) => (
                            <OverlayTrigger
                                key={index}
                                placement="right"
                                overlay={isCollapsed ? renderTooltip(item.label) : <></>}
                                delay={{ show: 200, hide: 100 }}
                            >
                                <li
                                    className={`nav-item ${
                                        location.pathname === item.path ? "active" : ""
                                    }`}
                                >
                                    <Link to={item.path} className="nav-link">
                                        <span className="nav-icon">{item.icon}</span>
                                        {!isCollapsed && (
                                            <span className="nav-label">{item.label}</span>
                                        )}
                                    </Link>
                                </li>
                            </OverlayTrigger>
                        ))}
                    </ul>
                </div>

                {/* Footer del Sidebar */}
                <div className="sidebar-footer">
                    <ul className="nav">
                        {sidebarData.footerItems.map((item, index) => (
                            <OverlayTrigger
                                key={index}
                                placement="right"
                                overlay={isCollapsed ? renderTooltip(item.label) : <></>}
                                delay={{ show: 200, hide: 100 }}
                            >
                                <li
                                    className={`nav-item ${
                                        location.pathname === item.path ? "active" : ""
                                    }`}
                                >
                                    <Link to={item.path} className="nav-link">
                                        <span className="nav-icon">{item.icon}</span>
                                        {!isCollapsed && (
                                            <span className="nav-label">{item.label}</span>
                                        )}
                                    </Link>
                                </li>
                            </OverlayTrigger>
                        ))}
                    </ul>
                </div>
            </div>

            {/* Botón para colapsar o expandir el Sidebar */}
            <button
                className="toggle-btn-circle"
                onClick={toggleSidebar}
                style={{ left: isCollapsed ? "75px" : "255px" }}
            >
                {isCollapsed ? <FaChevronRight /> : <FaChevronLeft />}
            </button>
        </div>
    );
};

export default Sidebar;
