import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../estilos/Sidebar.css";
import sidebarConfig from "../usuarios/administrador/configuracion/sidebarConfig";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";
import logo from "../recursos/logo.png";

const Sidebar = () => {
    const [isCollapsed, setIsCollapsed] = useState(false); // Controla si el sidebar está colapsado
    const [activeMenu, setActiveMenu] = useState(null); // Controla qué submenú está abierto

    const toggleSidebar = () => {
        setIsCollapsed(!isCollapsed); // Alterna entre expandido y colapsado
        setActiveMenu(null); // Cierra cualquier submenú abierto
    };

    const toggleSubMenu = (index) => {
        setActiveMenu(activeMenu === index ? null : index); // Alterna el submenú
    };

    return (
        <div className={`d-flex flex-column sidebar-container ${isCollapsed ? "collapsed" : ""}`}>
            {/* Botón de colapsar/expandir */}
            <button
                className="btn toggle-btn"
                onClick={toggleSidebar}
                title={isCollapsed ? "Expandir" : "Colapsar"}
                data-bs-toggle="tooltip"
                data-bs-placement="right"
            >
                {isCollapsed ? <FaArrowRight /> : <FaArrowLeft />}
            </button>

            {/* Sidebar */}
            <div className="sidebar bg-primary text-light">
                {/* Header con logo */}
                <div className="sidebar-header d-flex align-items-center p-3">
                    <img src={logo} alt="Logo" className="sidebar-logo" />
                    {!isCollapsed && <h3 className="sidebar-title ms-2">Parques de la Huaca</h3>}
                </div>

                {/* Menú principal */}
                <ul className="nav flex-column">
                    {sidebarConfig.map((item, index) => (
                        <li
                            key={index}
                            className={`nav-item ${item.subItems ? "has-submenu" : ""}`}
                        >
                            <NavLink
                                to={!item.subItems ? item.path : "#"}
                                className="nav-link d-flex align-items-center px-3 py-2"
                                onClick={() => item.subItems && toggleSubMenu(index)}
                                data-bs-toggle={isCollapsed ? "tooltip" : ""}
                                data-bs-placement="right"
                                title={isCollapsed ? item.label : ""}
                            >
                                <i className="nav-icon me-2">{item.icon}</i>
                                {!isCollapsed && <span>{item.label}</span>}
                            </NavLink>

                            {/* Submenú */}
                            {item.subItems && activeMenu === index && (
                                <ul className="sub-menu list-unstyled position-absolute">
                                    {item.subItems.map((subItem, subIndex) => (
                                        <li key={subIndex} className="sub-menu-item">
                                            <NavLink
                                                to={subItem.path}
                                                className="nav-link px-3 py-2 text-light"
                                            >
                                                {subItem.label}
                                            </NavLink>
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Sidebar;
