// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './componentes-comunes/Sidebar';  // Ruta correcta
import Navbar from './componentes-comunes/Navbar';    // Ruta correcta
import Footer from './componentes-comunes/Footer';    // Ruta correcta
import Dashboard from './administrador/paginas/Dashboard';  // Ruta correcta

const App = () => {
    return (
        <Router>
            <div className="app-container">
                <Sidebar />  {/* El Sidebar se renderiza siempre */}
                <div className="main-content">
                    <Navbar />
                    <Routes>
                        <Route path="/dashboard" element={<Dashboard />} />
                        {/* Puedes agregar más rutas aquí si es necesario */}
                    </Routes>
                    <Footer />
                </div>
            </div>
        </Router>
    );
};

export default App;
