import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../recursos/estilos/SidebarGlobal.css'; // Ruta desde `componentes-comunes` a `recursos/estilos`

const Sidebar = ({ sidebarData, isCollapsed, toggleSidebar }) => {
    const location = useLocation();

    return (
        <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
            {/* Header del Sidebar */}
            <div className="sidebar-header">
                <img src={sidebarData.logo} alt="Logo" className="sidebar-logo" />
                {!isCollapsed && <h3>{sidebarData.title}</h3>}
            </div>

            {/* Opciones principales */}
            <div className="sidebar-main-content">
                <ul className="nav">
                    {sidebarData.navItems.map((item, index) => (
                        <li
                            key={index}
                            className={`nav-item ${
                                location.pathname === item.path ? 'active' : ''
                            }`}
                        >
                            <Link to={item.path} className="nav-link">
                                <span className="nav-icon">{item.icon}</span>
                                {!isCollapsed && <span className="nav-label">{item.label}</span>}
                            </Link>
                        </li>
                    ))}
                </ul>
            </div>

            {/* Footer del Sidebar */}
            <div className="sidebar-footer">
                <ul className="nav">
                    {sidebarData.footerItems.map((item, index) => (
                        <li key={index} className="nav-item">
                            <Link to={item.path} className="nav-link">
                                <span className="nav-icon">{item.icon}</span>
                                {!isCollapsed && <span className="nav-label">{item.label}</span>}
                            </Link>
                        </li>
                    ))}
                </ul>
            </div>

            {/* Botón para colapsar el Sidebar */}
            <button
                className="toggle-btn-circle"
                onClick={toggleSidebar}
                style={{ left: isCollapsed ? '75px' : '255px' }}
            >
                {isCollapsed ? '→' : '←'}
            </button>
        </div>
    );
};

export default Sidebar;
