// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './componentes-comunes/Sidebar';
import Navbar from './componentes-comunes/Navbar';
import Footer from './componentes-comunes/Footer';
import Dashboard from './administrador/paginas/Dashboard';
import Usuarios from './administrador/paginas/Usuarios';
import Configuracion from './administrador/paginas/Configuracion';

const App = () => {
    return (
        <Router>
            <div className="app-container">
                <Sidebar /> {/* El Sidebar se renderiza siempre */}
                <div className="main-content">
                    <Navbar />
                    <Routes>
                        <Route path="/dashboard" element={<Dashboard />} />
                        <Route path="/usuarios" element={<Usuarios />} />
                        <Route path="/configuracion" element={<Configuracion />} />
                        {/* Puedes agregar más rutas aquí si es necesario */}
                    </Routes>
                    <Footer />
                </div>
            </div>
        </Router>
    );
};

export default App;
