import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Navbar from './componentes/Navbar';
import Sidebar from './componentes/Sidebar';
import './estilos/App.css'; // Puedes agregar estilos globales aquí

// Importar las páginas o componentes principales del administrador
import Usuarios from './usuarios/administrador/paginas/Usuarios';
import Propiedades from './usuarios/administrador/paginas/Propiedades';
// Importa otras páginas según las necesites

function App() {
    // Suponiendo que obtienes el rol del usuario autenticado
    const userRole = 'administrador'; // Puedes reemplazarlo con lógica de autenticación real

    return (
        <Router>
            <div className="app-container">
                <Navbar />
                <Sidebar userRole={userRole} />
                <div className="content">
                    <Switch>
                        {/* Define las rutas para el administrador */}
                        <Route path="/usuarios" component={Usuarios} />
                        <Route path="/propiedades" component={Propiedades} />
                        {/* Agrega más rutas según tus páginas */}
                        <Redirect to="/usuarios" />
                    </Switch>
                </div>
            </div>
        </Router>
    );
}

export default App;
