import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import SidebarAdmin from './administrador/componentes/SidebarAdmin';
import SidebarResidente from './residente/componentes/SidebarResidente';
import SidebarSeguridad from './personal-de-seguridad/componentes/SidebarSeguridad';
import DashboardAdmin from './administrador/paginas/Dashboard';
import DashboardResidente from './residente/paginas/Dashboard';
import DashboardSeguridad from './personal-de-seguridad/paginas/Dashboard';
import './App.css';

const App = () => {
    const [userType, setUserType] = useState('admin'); // Cambia esto para simular el tipo de usuario ('admin', 'residente', 'seguridad')
    const [isCollapsed, setIsCollapsed] = useState(false);

    const toggleSidebar = () => {
        setIsCollapsed(!isCollapsed);
    };

    const renderSidebar = () => {
        if (userType === 'admin') {
            return <SidebarAdmin isCollapsed={isCollapsed} toggleSidebar={toggleSidebar} />;
        } else if (userType === 'residente') {
            return <SidebarResidente isCollapsed={isCollapsed} toggleSidebar={toggleSidebar} />;
        } else if (userType === 'seguridad') {
            return <SidebarSeguridad isCollapsed={isCollapsed} toggleSidebar={toggleSidebar} />;
        }
    };

    const renderDashboard = () => {
        if (userType === 'admin') return <DashboardAdmin />;
        if (userType === 'residente') return <DashboardResidente />;
        if (userType === 'seguridad') return <DashboardSeguridad />;
    };

    return (
        <Router>
            <div className="app-container">
                {renderSidebar()}
                <div className={`main-content ${isCollapsed ? 'collapsed' : ''}`}>
                    <Routes>
                        <Route path="/dashboard" element={renderDashboard()} />
                        {/* Agrega las rutas correspondientes a cada tipo de usuario aqu√≠ */}
                    </Routes>
                </div>
            </div>
        </Router>
    );
};

export default App;
