// src/componentes-comunes/Sidebar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../recursos/estilos/SidebarGlobal.css';
import { SidebarAdminData } from '../administrador/componentes/data/SidebarAdminData';

const Sidebar = () => {
    const [subnavState, setSubnavState] = useState({});

    const showSubnav = (index) => {
        setSubnavState((prevState) => ({
            ...prevState,
            [index]: !prevState[index],
        }));
    };

    return (
        <div className="sidebar">
            <ul className="sidebar-nav">
                {SidebarAdminData.map((item, index) => (
                    <li key={index} className="sidebar-item">
                        <div onClick={() => item.subNav && showSubnav(index)}>
                            <Link to={item.path || '#'}>
                                <div className="sidebar-title">
                                    {item.icon && <span className="icon">{item.icon}</span>}
                                    <span>{item.label}</span>
                                </div>
                                {item.subNav && (
                                    <span className="icon-arrow">
                    {subnavState[index] ? item.iconOpened : item.iconClosed}
                  </span>
                                )}
                            </Link>
                        </div>
                        {item.subNav && subnavState[index] && (
                            <ul className="subnav">
                                {item.subNav.map((subItem, subIndex) => (
                                    <li key={subIndex} className="subnav-item">
                                        <Link to={subItem.path}>
                                            {subItem.icon && <span className="icon">{subItem.icon}</span>}
                                            <span>{subItem.label}</span>
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Sidebar;
