import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "../estilos/Sidebar.css";
import sidebarConfig from "../usuarios/administrador/configuracion/sidebarConfig";

const Sidebar = () => {
    const [isCollapsed, setIsCollapsed] = useState(false);
    const [activeMenu, setActiveMenu] = useState(null);

    const toggleSidebar = () => {
        setIsCollapsed(!isCollapsed);
    };

    const toggleSubMenu = (index) => {
        setActiveMenu(activeMenu === index ? null : index);
    };

    return (
        <>
            <button
                className="toggle-btn-circle"
                onClick={toggleSidebar}
                style={{ left: isCollapsed ? "70px" : "250px" }}
            >
                <i className={`fas ${isCollapsed ? "fa-arrow-right" : "fa-arrow-left"}`}></i>
            </button>
            <div className={`sidebar-container ${isCollapsed ? "collapsed" : ""}`}>
                <div className={`sidebar ${isCollapsed ? "collapsed" : ""}`}>
                    <div className="sidebar-header">
                        <img
                            src="/src/recursos/logo.png"
                            alt="Logo"
                            className="sidebar-logo"
                        />
                        <h3>Administrador</h3>
                    </div>
                    <div className="sidebar-main-content">
                        <ul className="nav">
                            {sidebarConfig.map((item, index) => (
                                <li
                                    key={index}
                                    className={`nav-item ${item.subItems ? "has-submenu" : ""}`}
                                >
                                    <NavLink
                                        to={item.path}
                                        className="nav-link"
                                        activeClassName="active"
                                        onClick={() =>
                                            item.subItems ? toggleSubMenu(index) : setActiveMenu(null)
                                        }
                                    >
                                        <i className={`nav-icon ${item.icon}`}></i>
                                        <span className="nav-label">{item.label}</span>
                                        {item.subItems && (
                                            <i
                                                className={`fas ${
                                                    activeMenu === index ? "fa-chevron-up" : "fa-chevron-down"
                                                } submenu-icon`}
                                            ></i>
                                        )}
                                    </NavLink>
                                    {item.subItems && activeMenu === index && (
                                        <ul className="nav sub-menu">
                                            {item.subItems.map((subItem, subIndex) => (
                                                <li key={subIndex} className="nav-item">
                                                    <NavLink
                                                        to={subItem.path}
                                                        className="nav-link"
                                                        activeClassName="active"
                                                    >
                                                        {subItem.label}
                                                    </NavLink>
                                                </li>
                                            ))}
                                        </ul>
                                    )}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Sidebar;
