import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css"; // Importar Bootstrap
import "bootstrap/dist/js/bootstrap.bundle.min"; // Importar funcionalidades de Bootstrap
import "../estilos/Sidebar.css";
import sidebarConfig from "../usuarios/administrador/configuracion/sidebarConfig";
import { FaArrowLeft, FaArrowRight, FaChevronRight } from "react-icons/fa";
import logo from "../recursos/logo.png";

const Sidebar = () => {
    const [isCollapsed, setIsCollapsed] = useState(false); // Controla si el sidebar está colapsado
    const [activeMenu, setActiveMenu] = useState(null); // Controla el menú activo

    const toggleSidebar = () => {
        setIsCollapsed(!isCollapsed); // Alterna entre expandido y colapsado
        setActiveMenu(null); // Cierra cualquier submenú abierto
    };

    const toggleSubMenu = (index) => {
        setActiveMenu(activeMenu === index ? null : index); // Alterna el submenú
    };

    return (
        <div className={`sidebar-container ${isCollapsed ? "collapsed" : ""}`}>
            {/* Botón para expandir/colapsar */}
            <button
                className="toggle-btn-circle"
                onClick={toggleSidebar}
                style={{ left: isCollapsed ? "70px" : "250px" }}
                title={isCollapsed ? "Expandir" : "Colapsar"}
            >
                {isCollapsed ? <FaArrowRight /> : <FaArrowLeft />}
            </button>

            {/* Sidebar principal */}
            <div className="sidebar">
                {/* Encabezado */}
                <div className="sidebar-header">
                    <img src={logo} alt="Logo" className="sidebar-logo" />
                    {!isCollapsed && <h3 className="sidebar-title">Parques de la Huaca</h3>}
                </div>

                {/* Opciones de menú */}
                <div className="sidebar-main-content">
                    <ul className="nav">
                        {sidebarConfig.map((item, index) => (
                            <li
                                key={index}
                                className={`nav-item ${item.subItems ? "has-submenu" : ""}`}
                                onMouseEnter={() =>
                                    isCollapsed && item.subItems && setActiveMenu(index)
                                }
                                onMouseLeave={() =>
                                    isCollapsed && item.subItems && setActiveMenu(null)
                                }
                                onClick={() =>
                                    !isCollapsed && item.subItems && toggleSubMenu(index)
                                }
                            >
                                <NavLink
                                    to={!item.subItems ? item.path : "#"}
                                    className="nav-link"
                                    data-bs-toggle={isCollapsed ? "tooltip" : ""}
                                    data-bs-placement="right"
                                    title={isCollapsed ? item.label : ""}
                                >
                                    <i className="nav-icon">{item.icon}</i>
                                    {!isCollapsed && (
                                        <span className="nav-label">{item.label}</span>
                                    )}
                                    {item.subItems && !isCollapsed && (
                                        <i className="submenu-icon">
                                            <FaChevronRight />
                                        </i>
                                    )}
                                </NavLink>

                                {/* Submenú */}
                                {item.subItems && (
                                    <ul
                                        className={`sub-menu ${
                                            activeMenu === index ? "visible" : ""
                                        } ${isCollapsed ? "collapsed-submenu" : ""}`}
                                    >
                                        {item.subItems.map((subItem, subIndex) => (
                                            <li key={subIndex} className="sub-menu-item">
                                                <NavLink
                                                    to={subItem.path}
                                                    className="sub-menu-link"
                                                >
                                                    {subItem.label}
                                                </NavLink>
                                            </li>
                                        ))}
                                    </ul>
                                )}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default Sidebar;
