import React from 'react';
import InfoCard from '../componentes/InfoCard';
import './Dashboard.css';

const Dashboard = () => {
    // Datos para las tarjetas
    const cardsData = [
        {
            icon: 'shield', // Icono
            title: 'Incidentes de Seguridad',
            subtitle: '1 Incidente Registrado',
            linkText: 'Ver incidentes',
            linkPath: '/incidentes',
        },
        {
            icon: 'tools',
            title: 'Solicitudes de Mantenimiento',
            subtitle: '8 Pendientes',
            linkText: 'Ver todas las solicitudes',
            linkPath: '/mantenimiento',
        },
        {
            icon: 'user',
            title: 'Visitas Recientes',
            subtitle: '3 Autorizados',
            linkText: 'Ver detalles de acceso',
            linkPath: '/visitas',
        },
        {
            icon: 'file-alt',
            title: 'Boletas de Pago Subidas',
            subtitle: '10 Nuevas',
            linkText: 'Ver todas las boletas',
            linkPath: '/boletas',
        },
        {
            icon: 'envelope',
            title: 'Correspondencia Recibida',
            subtitle: '2 Paquetes',
            linkText: 'Ver toda la correspondencia',
            linkPath: '/correspondencia',
        },
        {
            icon: 'calendar',
            title: 'Reservas Próximas',
            subtitle: '5 Áreas Comunes Reservadas',
            linkText: 'Gestionar reservas',
            linkPath: '/reservas',
        },
        {
            icon: 'calendar-alt',
            title: 'Eventos del Condominio',
            subtitle: '2 Próximos',
            linkText: 'Ver eventos',
            linkPath: '/eventos',
        },
    ];

    return (
        <div className="dashboard">
            <h2>Buenos días, [Usuario]</h2>
            <div className="dashboard-cards">
                {cardsData.map((card, index) => (
                    <InfoCard
                        key={index}
                        icon={card.icon}
                        title={card.title}
                        subtitle={card.subtitle}
                        linkText={card.linkText}
                        linkPath={card.linkPath}
                    />
                ))}
            </div>
        </div>
    );
};

export default Dashboard;
