import React, { useState } from 'react';
import { maintenanceData } from '../config/MaintenanceData'; // Datos simulados
import MaintenanceTable from '../componentes/MaintenanceTable';
import '../styles/global.css'; // Uso del CSS global

const Mantenimiento = () => {
    const [pendingRequests, setPendingRequests] = useState(
        maintenanceData.filter((request) => request.estado === 'Pendiente')
    );

    const [historyRequests, setHistoryRequests] = useState(
        maintenanceData.filter((request) => request.estado !== 'Pendiente')
    );

    return (
        <div className="page-container">
            {/* Encabezado de la página */}
            <div className="page-header">
                <h2>Solicitudes de Mantenimiento</h2>
                <button className="action-btn">
                    <i className="fas fa-plus-circle"></i> Añadir Nueva Solicitud
                </button>
            </div>

            {/* Tabla de solicitudes pendientes */}
            <div>
                <h3>Solicitudes Pendientes</h3>
                <MaintenanceTable data={pendingRequests} showTechnician={false} />
            </div>

            {/* Historial de solicitudes */}
            <div style={{ marginTop: '40px' }}>
                <h3>Historial de Solicitudes</h3>
                <MaintenanceTable data={historyRequests} showTechnician={true} />
            </div>
        </div>
    );
};

export default Mantenimiento;
